`;
export const ListProductWrapper = styled.div`
    border: 1px solid transparent;
    border-bottom-color: #ddd;
    margin: 0;
    .list-tab {
        display: flex;
    }
`
export const TabItems = styled.li`
    color: #000;
    transition: .3s;
    padding: 0.85rem 1rem;
    &.active-tab {
        background: #020202;
        color: #fff;
    }
`
