import React from "react";
import { useState } from "react";
import { NavLink } from "react-router-dom";
import { MenuItems, SubMenuItems } from "./style";
export const SubMenuItem  =({subMenuItem}) => {
  const {name, path} = subMenuItem;
  const [status, setStatus] = useState(true);
  return(
    <SubMenuItems className="sub-menu-item" status={status}>
      <NavLink className="sub-menu-link" activeClassName="active" to={path} exact>
        {name}
      </NavLink>
    </SubMenuItems>
  );
}
const MenuItem = ({menuItem, type}) => {
  const {name, path, icon, subMenu}  = menuItem;
  return(
    <MenuItems className="menu-item" type={type} subMenu={subMenu}>
      <NavLink to={path} exact className="menu-link" activeClassName="active">
        {icon}
        {name}
      </NavLink>
      {subMenu && <ul className="sub-menu">
        {subMenu.map(item => {
          return <SubMenuItem subMenuItem={item} key={item.id}/>
        })}
      </ul>}
    </MenuItems>
  );
}
export default MenuItem;
