import React, { useEffect, useState } from "react";
import Buttons from "../Button";
import Input from './../input'
import { HeaderWrapper, HeaderBottomWrapper, CardMobile, MenuBottomRight } from "./style";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch,  faUser, faTimes } from "@fortawesome/free-solid-svg-icons"
import { listMenu } from "./data";
import { NavLink } from "react-router-dom";
import HeaderBottomItem from "./headerBottomItem";
import IconCard from "./iconCard";
import { ProductTabs } from "../../container/Home/data";
import SearchProductItem from "./searchProductItem";
import _ from 'lodash';
import { SocialItem, socialFooter } from './../Footer';

const Header = props => {
    // quản lý trạng thái search

    //Quản lý trạng thái: input search , các item menu, trạng thái của icon menu hoặc tab menu ở màn mobile
    const [state, setState] = useState({
        search: '',
        listMenus: [],
        status: false,
        products: []
    })
    // quản lý trạng thái input search
    const [inputSearch,setInputSearch] = useState(false);



    // Quản lý trạng thái thanh card
    const [statusCard, setStatusCard] = useState(false);
    useEffect(() => {
        // lấy data
        setState({
            ...state,
            listMenus: listMenu,
            products: ProductTabs
        })
    }, [])
    const { products } = state;

    //quản lý trang thái dropdown menu
    const [statusMenuItem, setStatusMenuItem] = useState(true);




    // Quản lý keyword khi nhập vào nút search
    const [keyword, setKeyword] = useState('');
    // quản lý mảng khi tìm kiếm
    const [filterSearch, setFilterSearch] = useState([]);

    // quản lý onChange input
    const handleInputChange = event => {
        const { name, value } = event.target;
        setKeyword(value);
        const newValue = value.toLowerCase();
        const newSearch = _.filter(products, item => item.name.toLowerCase().includes(newValue));
        const empty = [{ name: '' }]
        const check = [...newSearch.length ? newSearch : empty]
        setFilterSearch(check);
        setState({
            ...state,
            [name]: value
        })
    }
    const data = keyword ? filterSearch : products;
    // quản lý icon mmenu
    const handleStatus = () => {
        setState({
            ...state,
            status: !state.status
        })
        setStatusCard(false);

    }
    //xử lý phần card
    const handleStatusCard = () => {
        setStatusCard(!statusCard);
        setState({
            ...state,
            status: false
        })
    }
    const { listMenus } = state;
    // xử lý tab menu bottom
    const handleCloseMenu = () => {
        setState({
            ...state,
            status: false
        })

    }
    // hiển thị giao diện search
    const [statusSearch, setStatusSearch] = useState(true)
    // xử lý click vào sản phẩm thì tắt tab search đi
    const handleCloseSearch = () => {
        setStatusSearch(true);
        setState({
            ...state,
            search: ''
        })
    }
    const handleClickToHome = () => {

    }
    console.log('dataaa', data);
    useEffect(() => {
        const loga = document.querySelector(".account");
        const body = document.querySelector("body");
        // loga.onClick = function() {
        //     alert("ac")
        // }
        console.log('loga', loga);
        loga.addEventListener("click", () => {
            loga.classList.add("abc");
        })
        body.addEventListener("click", () => {
            loga.classList.add("abcd");
        })
    }, []);
    const handleClickShowInputSearch = () => {
        setInputSearch(true);
        const body = document.querySelector("body");
        
    }
    return (
        <HeaderWrapper className="header" status={statusSearch}>
            <div className="header-top">
                <div className="header-top-left">
                    <NavLink exact to="/login">
                        <Buttons name="Đăng Nhập" type="login" onClick={handleCloseMenu} className="login" />
                    </NavLink>
                    <NavLink exact to="/register">
                        <Buttons name="Đăng Ký" type="register" onClick={handleCloseMenu} className="register"></Buttons>
                    </NavLink>
                    <p className="phone">
                        0392272154 | 0376326810
                    </p>
                </div>





                <div className="group-card">
                    {/* <ul className="list-social">
                        {socialFooter.map((item, index) => {
                            return <SocialItem item={item} key={index} />
                        })}
                    </ul> */}
                    {/* <div className="search">
                        <span className="icon-search" onClick={handleStatusSearch}>
                            <FontAwesomeIcon icon={faSearch} />
                        </span>
                        <div className="input-search">
                            <Input type="text" className="form-control" name="search" value={state.search} onChange={handleInputChange} placeholder="Tìm kiếm..." />
                            {state.search === '' ? '' : data.map(item => {
                                return item.name !== '' ?
                                    <SearchProductItem onClick={handleCloseSearch} item={item} key={item.id} empty={filterSearch} /> :
                                    <p className="empty">Sản phẩm không tồn tại !!!</p>
                            })}

                        </div>
                    </div>
                    <div className="card">
                        <IconCard className="icon-card" name="0" />
                    </div> */}
                    {/* <CardMobile className="card-modile" status={statusCard}>

                        <IconCard className="icon-card" name="0" onClick={handleStatusCard} />

                        <div className="show-card">
                            <span className="close-card" onClick={handleStatusCard}>
                                <FontAwesomeIcon icon={faTimes} />
                            </span>
                            <h2 className="title-card">
                                Giỏ Hàng
                            </h2>
                            <span className="empty">
                                Chưa có sản phẩm trong giỏ hàng.
                            </span>
                        </div>
                    </CardMobile> */}

                </div>
            </div>
            <NavLink exact to="/" className="logo logo-mobile">
                <img src="/assets/images/logo/1.png" alt="logo" />
            </NavLink>
            <HeaderBottomWrapper className="header-bottom" status={state.status}>
                <span className="icon-menu" onClick={handleStatus}>
                    <span className="icon-menu-item"></span>
                </span>
                <NavLink exact to="/" className="logo">
                    <img src="/assets/images/logo/1.png" alt="logo" />
                </NavLink>
                <ul className="list-menu">
                    <li className="close-menu" onClick={handleCloseMenu}>
                        <FontAwesomeIcon icon={faTimes} />
                    </li>
                    <li className="register-login">
                        <img src="/assets/images/register/user.png" alt="user" />
                        <div className="btn-login-register">
                            <NavLink exact to="/login">
                                <Buttons className="btn btn-primary" onClick={handleCloseMenu} type="login-moblie" name="Đăng nhập" />
                            </NavLink>
                            <NavLink exact to="/register">
                                <Buttons className="btn btn-primary" onClick={handleCloseMenu} type="register-moblie" name="Đăng Ký" />
                            </NavLink>
                        </div>
                    </li>
                    {listMenus.map(item => {
                        return <HeaderBottomItem item={item} key={item.id} status={statusMenuItem} onClick={handleCloseMenu} />

                    })}
                </ul>
                <MenuBottomRight className="menu-bottom-right" >
                    <div className="search">
                        <span onClick={handleClickShowInputSearch}>
                            <FontAwesomeIcon icon={faSearch} />
                        </span>
                        {inputSearch && <div className="input-search">
                            <span className="close-screen-search">
                                <FontAwesomeIcon icon={faTimes}/>
                            </span>
                            <Input
                                type="text"
                                className="form-control"
                                name="search"
                                value={state.search}
                                onChange={handleInputChange}
                                placeholder="Tìm kiếm..."

                            />
                            {state.search === '' ? '' : <div className="result-search">
                                {
                                    data.map((item,index) => {
                                        return item.name !==  "" ?
                                            <SearchProductItem onClick={handleCloseSearch} item={item} key={item.id} empty={filterSearch} /> :
                                            <div key={index} className="empty">
                                                 <img src="/Assets/Images/product/empty_product.svg" alt="empty" />
                                                <p>Sản phẩm không tồn tại !!!</p>
                                            </div>
                                    })
                                }
                            </div>}

                        </div>}
                    </div>
                    <div className="card-shop">
                        <span>
                            <IconCard className="icon-card" name="0" />
                        </span>
                    </div>
                    <div className="account">
                        <span><FontAwesomeIcon icon={faUser} /></span>
                    </div>
                </MenuBottomRight>
            </HeaderBottomWrapper>
        </HeaderWrapper>
    );
}
export default Header;
