import styled from "styled-components";
export const HeaderTopItems = styled.li`
  & > a {
    color: #fff;
    padding: .35rem 1rem;
    display: block;
    text-transform: uppercase;
    &:before {
      content: "${props => props.name === 'đăng nhập'
      ? '\f007' : (props.name==="đăng ký" ? '\f234' : '')}";

    }
  }
`
