import styled from "styled-components";
export const Title = styled.h3`
    padding: 0.55rem 1rem;
    margin: 0;
    color: #fff;
    text-transform: uppercase;
    position: relative;
    &:after {
        width: 30%;
        height: 3px;
        ba
        position: absolute;
        top: 0;
        left: 0;
    }
`
