import React, { useEffect } from "react";
import {
  getListProductDetailRequest
} from "./action";
import { connect } from "react-redux";
import {
  selectDetail,
  selectIsLoading
} from "./selector"
import Loading from "../Components/Loading";
import { DetailWrapper } from "./style";
import NamePage from "../Components/NamePage";

const Detail = (props) => {
  const {
    handleGetListProductDetail,
    history,
    match: { params: { id } },
    detail,
    isLoading
  } = props;
  useEffect(() => {
    handleGetListProductDetail(+id)
  }, [])
  if (isLoading) return <Loading />
  // hiển thị info
  const {
    avatar,
    color,
    description,
    discount,
    discountContent,
    evaluate,
    like,
    name,
    price,
    promotion,
    quantity,
    questionAnswer,
    ship,
    size,
  } = detail;
  const handleClickToHome = () => {
    history.push("/");
  }
  return (
    <DetailWrapper className="detail-wrapper">
      <div className="container">
        <h3 className="back" onClick={handleClickToHome}>
          Quay lại trang chủ
        </h3>
      </div>
    </DetailWrapper>
  );
}
const mapStateToProps = state => {
  return {
    isLoading: selectIsLoading(state),
    detail: selectDetail(state)
  }
}
const mapDispatchToProps = dispatch => {
  return {
    handleGetListProductDetail: data => dispatch(getListProductDetailRequest(data))
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(Detail);
