import React, { useEffect, useState } from "react";
import Buttons from "../Button";
import Input from './../input'
import { HeaderWrapper, HeaderBottomWrapper, CardMobile, MenuBottomRight, HeaderTopWrapper } from "./style";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch, faUser, faTimes } from "@fortawesome/free-solid-svg-icons"
import { listMenuLeft, listMenuRight, listHeaderTopLeft, listHeaderTopRight } from "./data";
import { NavLink } from "react-router-dom";
import HeaderBottomItem from "./headerBottomItem";
import IconCard from "./iconCard";
import { ProductTabs } from "../../container/Home/data";
import SearchProductItem from "./searchProductItem";
import _ from 'lodash';
import { SocialItem, socialFooter } from './../Footer';
import HeaderTopItem from "./HeaderTopItem";

const Header = ({ activeColor }) => {
    // quản lý trạng thái search

    //Quản lý trạng thái: input search , các item menu, trạng thái của icon menu hoặc tab menu ở màn mobile
    const [state, setState] = useState({
        search: '',
        listMenusLeft: [],
        listMenusRight: [],
        HeaderTopLefts: [],
        HeaderTopRights: [],
        status: false,
        products: [],
        origin: false
    })
    // quản lý trạng thái input search
    const [inputSearch, setInputSearch] = useState(false);



    // Quản lý trạng thái thanh card
    const [statusCard, setStatusCard] = useState(false);
    useEffect(() => {
        // lấy data
        setState({
            ...state,
            listMenusLeft: listMenuLeft,
            listMenusRight: listMenuRight,
            HeaderTopLefts: listHeaderTopLeft,
            HeaderTopRights: listHeaderTopRight,
            products: ProductTabs
        })
    }, [])
    const { products } = state;

    //quản lý trang thái dropdown menu
    const [statusMenuItem, setStatusMenuItem] = useState(true);




    // Quản lý keyword khi nhập vào nút search
    const [keyword, setKeyword] = useState('');
    // quản lý mảng khi tìm kiếm
    const [filterSearch, setFilterSearch] = useState([]);

    // quản lý onChange input
    const handleInputChange = event => {
        const { name, value } = event.target;
        setKeyword(value);
        const newValue = value.toLowerCase();
        const newSearch = _.filter(products, item => item.name.toLowerCase().includes(newValue));
        const empty = [{ name: '' }]
        const check = [...newSearch.length ? newSearch : empty]
        setFilterSearch(check);
        setState({
            ...state,
            [name]: value
        })
    }
    const data = keyword ? filterSearch : products;
    // quản lý icon mmenu
    const handleStatus = () => {
        setState({
            ...state,
            status: !state.status
        })
        setStatusCard(false);

    }
    //xử lý phần card
    const handleStatusCard = () => {
        setStatusCard(!statusCard);
        setState({
            ...state,
            status: false
        })
    }
    const { listMenusLeft, listMenusRight, HeaderTopLefts, HeaderTopRights } = state;
    // xử lý tab menu bottom
    const handleCloseMenu = () => {
        setState({
            ...state,
            status: false
        })

    }
    // hiển thị giao diện search
    const [statusSearch, setStatusSearch] = useState(true)
    // xử lý click vào sản phẩm thì tắt tab search đi
    const handleCloseSearch = () => {
        setStatusSearch(true);
        setState({
            ...state,
            search: ''
        })
    }
    const handleClickToHome = () => {

    }
    /*
        JS DOM
    */
    const body = document.querySelector("body");


    // menu mobile
    const listMenuMobile = _.concat(state.listMenusLeft, state.listMenusRight)
    // useEffect quản lý thanh cuộn header
    useEffect(() => {
        const headerBottom = document.querySelector(".header-bottom");
        const originHeaderBottom = headerBottom.offsetHeight;
        window.addEventListener("scroll", () => {
            const scrollBody = window.scrollY;
            setState({
                ...state,
                origin: body.clientWidth > 992
            })
            scrollBody > (originHeaderBottom * 2) ? headerBottom.classList.add("fixed") : headerBottom.classList.remove("fixed");
        })
    }, []);


    const handleClickShowInputSearch = () => {
        setInputSearch(true);
        body.classList.add("hidden");
        setState({
            ...state,
            search: ""
        })
    }
    const handleClickCloseInputSearch = () => {
        setInputSearch(false);
        body.classList.remove("hidden");
        setState({
            ...state,
            search: ""
        })
    }
    return (
        <HeaderWrapper activeColor={activeColor} className="header" status={statusSearch}>
            <HeaderTopWrapper className="header-top">
                <ul className="header-top-left">
                    {HeaderTopLefts.map(item => <HeaderTopItem headerTopItem={item} key={item.id} />)}

                </ul>
                <ul className="header-top-right">
                    <li className="header-top-item">
                        <Input autocomplete="off" type="text" name="search" className="input-search" placeholder="Tìm kiếm sản phẩm ..." />
                    </li>
                    {HeaderTopRights.map(item => <HeaderTopItem headerTopItem={item} key={item.id} />)}
                </ul>
            </HeaderTopWrapper>

            <HeaderBottomWrapper
                className="header-bottom"
                status={state.status} activeColor={activeColor}>
                    {/* icon menu mobile */}
                <span className="icon-menu" onClick={handleStatus}>
                    <span className="icon-menu-item"></span>
                </span>
                <NavLink exact to="/" className="logo-mobile">
                    <img src="/assets/images/logo/1.png" alt="logo" />
                </NavLink>

                <ul className="list-menu">
                    <li className="close-menu" onClick={handleCloseMenu}>
                        <FontAwesomeIcon icon={faTimes} />
                    </li>
                    <li className="register-login">
                        <img src="/assets/images/register/user.png" alt="user" />
                        <div className="btn-login-register">
                            <NavLink exact to="/login">
                                <Buttons className="btn btn-primary" onClick={handleCloseMenu} type="login-moblie" name="Đăng nhập" />
                            </NavLink>
                            <NavLink exact to="/register">
                                <Buttons className="btn btn-primary" onClick={handleCloseMenu} type="register-moblie" name="Đăng Ký" />
                            </NavLink>
                        </div>
                    </li>
                    {(listMenuLeft).map(item => {
                        return <HeaderBottomItem activeColor={activeColor} item={item} key={item.id} status={statusMenuItem} onClick={handleCloseMenu} />
                    })}
                </ul>
                <NavLink exact to="/" className="logo">
                    <img src="/assets/images/logo/1.png" alt="logo" />
                </NavLink>
                <MenuBottomRight className="menu-bottom-right" >
                    {/* <div className="search">
                        <span onClick={handleClickShowInputSearch}>
                            <FontAwesomeIcon icon={faSearch} />
                        </span>
                        {inputSearch && <div
                            className="input-search"
                            // onClick={handleClickCloseInputSearch}
                        >
                            <span
                                className="close-screen-search"
                                onClick={handleClickCloseInputSearch}>
                                <FontAwesomeIcon icon={faTimes} />
                            </span>
                            <Input
                                type="text"
                                className="form-control"
                                name="search"
                                value={state.search}
                                onChange={handleInputChange}
                                placeholder="Tìm kiếm..."

                            />
                            {state.search === '' ? '' : <div className="result-search">
                                {
                                    data.map((item, index) => {
                                        return item.name !== "" ?
                                            <SearchProductItem onClick={handleClickCloseInputSearch} item={item} key={item.id} empty={filterSearch} /> :
                                            <div key={index} className="empty">
                                                <img src="/Assets/Images/product/empty_product.svg" alt="empty" />
                                                <p>Sản phẩm không tồn tại !!!</p>
                                            </div>
                                    })
                                }
                            </div>}

                        </div>}
                    </div>
                    <div className="card-shop">
                        <span>
                            <IconCard className="icon-card" name="0" />
                        </span>
                    </div>
                    <div className="account">
                        <span><FontAwesomeIcon icon={faUser} /></span>
                    </div> */}
                    {listMenusRight.map(item => {
                        return <HeaderBottomItem activeColor={activeColor} item={item} key={item.id} status={statusMenuItem} onClick={handleCloseMenu} />

                    })}
                </MenuBottomRight>
            </HeaderBottomWrapper>
        </HeaderWrapper>
    );
}
export default Header;
