import styled from "styled-components";
const setBackground = type => {
    switch (type) {
        case 'register': {
            return 'transparent';
        }
        default:
            break;
    }
}
export const ButtonWrapper = styled.button`
    border: none;
    background: ${props => setBackground(props.type)};
    color: ${props => setColor};

`
