import styled from "styled-components";
export const PaginationPageWrapper  = styled.div`
    width: 70%;
    .empty {
        color: #5c5757;
        font-size: 3rem;
        width: 100%;
        height: 50vh;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        .empty-text {
            color: #cacaca;
        }
        .empty-item {
            color: transparent;
            -webkit-text-stroke: 3px #5c5757;
            font-weight: bold;
            font-size: 8rem;
            position: relative;
            line-height: 1;
            &:after,&:before {
                content: '4';
                -webkit-text-stroke: 3px #000;
            }
        }
    }
    .paginationBttns {
        display: flex;
        justify-content: center;
        margin: 1.5rem 0;
        &> li {
            padding: 0.25rem;
            color: #000;
            font-size: 1.4rem;
            &.paginationActive {
                color: #37b5ff;
            }
            &.paginationDisabled > a {
                color: #ddd !important;
            }
            &.next > .nextBttn, &.previous > .previousBttn {
                color: #37b5ff;
            }

        }
    }
`
