import styled from "styled-components";
export const MenuItems = styled.li`

  & > .menu-link {
    display: ${props => props.type ? 'flex' : 'block'};
    flex-direction: column;
    align-items: center;
    padding: .75rem 1rem;
    color: #000;
    text-transform: uppercase;
    font-weight: 550;
    & > svg {
      font-size: 1.2rem;
      margin: .35rem 0;
    }
    &.active {
      color: #ee4d2d;
    }
  }

`
