import React, { useEffect, useState } from "react";
import {
  getListProductDetailRequest
} from "./action";
import { connect } from "react-redux";
import {
  selectDetail,
  selectIsLoading
} from "./selector";
import Loading from "../Components/Loading";
import { SelectColorItems, DetailWrapper, IndicatorItems, ProductDetailWrapper } from "./style";
import _ from 'lodash';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faAngleLeft,
  faAngleRight,
  faStar
} from "@fortawesome/free-solid-svg-icons";
import { faHeart } from "@fortawesome/free-regular-svg-icons"
import Button from "./../../Component/Button";
import { formatMoney } from "./../../Component/Format";
const IndicatorItem = ({
  indicatorItem,
  active,
  handleClickChangeIndex
}) => {
  const { src } = indicatorItem;
  return (
    <IndicatorItems onClick={handleClickChangeIndex} className={`${active} indicator-item`}>
      <img src={src} alt="indicator" />
    </IndicatorItems>
  );
}

const SelectColorItem = ({ colorItem }) => {
  console.log(colorItem);
  return (
    <SelectColorItems className="color-item">
      {colorItem}
    </SelectColorItems>
  );
}

const Detail = (props) => {
  const {
    handleGetListProductDetail,
    history,
    match: { params: { id } },
    detail,
    isLoading
  } = props;
  const [index, setIndex] = useState(0);
  const [start, setStart] = useState(0);
  const [end, setEnd] = useState(3);
  useEffect(() => {
    handleGetListProductDetail(+id)
  }, [])
  if (isLoading) return <Loading />
  // hiển thị info
  const {
    avatar,
    color,
    description,
    discount,
    discountContent,
    evaluate,
    like,
    name,
    price,
    promotion,
    quantity,
    questionAnswer,
    ship,
    size,
  } = detail;
  const formatPrice = formatMoney(price);
  const formatDiscount = formatMoney(discount);
  const { brand, descriptContent, subImage } = description;
  const selectImage = subImage[index];
  const indicator = _.slice(subImage, start, end);
  const handleClickChangeIndex = currentIndex => {
    setIndex(currentIndex);
  }
  const handleClickNext = () => {
    setIndex(index + 1);
    if (index === end - 1) {
      setStart(start + 3);
      setEnd(end + 3);

    }
  }
  const handleClickPrev = () => {
    setIndex(index - 1);
    if (index === start) {
      setStart(start - 3);
      setEnd(end - 3);
    }
  }
  return (
    <DetailWrapper
      disabledPrev={index}
      disabledNext={index === subImage.length - 1}


      className="detail-wrapper">
      <div className="container">
        <ProductDetailWrapper className="product-detail">
          <div className="slider-images">
            <img
              src={selectImage.src}
              alt={selectImage.src}
            />
            <ul
              className="indicator">
              {indicator.map((item, index) => <IndicatorItem
                indicatorItem={item}
                key={item.id}
                active={selectImage.id === item.id && "active"}
                handleClickChangeIndex={
                  () => handleClickChangeIndex(index)
                }
              />)}
              <li className="slider-prev">
                <Button
                  name={<FontAwesomeIcon icon={faAngleLeft} />}
                  className={`btn-prev ${index === 0 && 'disabled'}`}
                  onClick={handleClickPrev}
                  disabled={index === 0}

                />
              </li>

              <li className="slider-next">
                <Button
                  name={<FontAwesomeIcon icon={faAngleRight} />}
                  className={`btn-next ${index === subImage.length - 1 && "disabled"}`}
                  onClick={handleClickNext}
                  disabled={index === subImage.length - 1}

                />
              </li>
            </ul>
          </div>
          <div className="select-product">
            <h3 className="name-product">
              {name}
            </h3>
            <div className="evaluate">
              <ul className="star">
                {[1, 2, 3, 4, 5].map(item => <li
                  key={item}
                  className="star-item"
                >
                  <FontAwesomeIcon icon={faStar} />
                </li>)}
                <li className="evaluate-text">
                  {evaluate} đánh giá
                </li>
              </ul>
              <span className="like">
                <FontAwesomeIcon icon={faHeart} />
              </span>
            </div>
            <h5 className="brand">
              Thương hiệu: <span className="name-brand">
                {brand}
              </span>
            </h5>
            <img
              className="sale"
              src="/assets/images/product/subImage/sale.webp"
              alt="sale" />
            <div className="price">
              <span className="price-item">
                {formatPrice} đ
              </span>
              <p className="price-discount">
                <span className="discount-item">
                  {formatDiscount} đ
                </span>
                <span className="percent-discount">
                  -{discountContent}%
                </span>
              </p>
            </div>
            <hr />
            <table cellSpacing={0} cellPadding={20}>
              <tbody>
                <tr>
                  <td>Nhóm màu</td>
                  <td>
                    <ul className="list-color">
                      {color.map(item => <SelectColorItem
                        key={item.id}
                        colorItem={item}
                      />)}
                    </ul>
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
          <div className="select-delivery">

          </div>
        </ProductDetailWrapper>
      </div>
    </DetailWrapper>
  );
}
const mapStateToProps = state => {
  return {
    isLoading: selectIsLoading(state),
    detail: selectDetail(state)
  }
}
const mapDispatchToProps = dispatch => {
  return {
    handleGetListProductDetail: data => dispatch(getListProductDetailRequest(data))
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(Detail);
