import styled from "styled-components";
export const LoginWrapper = styled.div`
    margin: 4rem;
    & > .container {
        width: 40%;
        display: flex;
        flex-direction: column;
        a
        .name-form {
            text-align: center;
            padding: 1rem;
            font-size: 2.5rem;
            text-transform: uppercase;
            color: #434E5E;
            position: relative;
            border: 2px solid red;
            display: inline-block;
        
        }
    }
`
