import styled from "styled-components";
export const FacebookWrapper = styled.div`
    text-align: center;
    background: red;
    width: max-content; 
    position: relative;
    z-index: 3;
    overflow: hidden;
    &:after {
        width: 100%;
        height: 100%;
        position: absolute;
        top: 0;
        left: 0;
        background: #4c69ba;
        content: 'Facebook';
        color: #fff;
        font-size: 1.3rem;
        padding-left: 2.3rem;

    }
    & > span button {
        z-index: 3;
        position: relative;
        opacity: 0;
        padding: 0.55rem 0rem;
    }
    &:before {
        position: absolute;
        top: 50%;
        left: 0;
        transform: translateY(-50%);
        content: '\f39e';
        z-index: 2;
        font-family: 'Fontawesome';
        color: #fff;
        padding: 1rem;
        border-right: 2px dashed #fff;

    }
`
