import React, { useEffect, useState } from "react";
import {
  getListProductDetailRequest
} from "./action";
import { connect } from "react-redux";
import {
  selectDetail,
  selectIsLoading
} from "./selector"
import Loading from "../Components/Loading";
import { DetailWrapper, IndicatorItems, ProductDetailWrapper } from "./style";
import _ from 'lodash';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAngleLeft, faAngleRight } from "@fortawesome/free-solid-svg-icons";
const IndicatorItem = ({
  indicatorItem,
  active,
  handleClickChangeIndex
}) => {
  const { src } = indicatorItem;
  return (
    <IndicatorItems onClick={handleClickChangeIndex} className={`${active} indicator-item`}>
      <img src={src} alt="indicator" />
    </IndicatorItems>
  );
}

const Detail = (props) => {
  const {
    handleGetListProductDetail,
    history,
    match: { params: { id } },
    detail,
    isLoading
  } = props;
  const [index, setIndex] = useState(0);
  const [start, setStart] = useState(0);
  const [end, setEnd] = useState(3);
  useEffect(() => {
    handleGetListProductDetail(+id)
  }, [])
  if (isLoading) return <Loading />
  // hiển thị info
  const {
    avatar,
    color,
    description,
    discount,
    discountContent,
    evaluate,
    like,
    name,
    price,
    promotion,
    quantity,
    questionAnswer,
    ship,
    size,
  } = detail;
  const handleClickToHome = () => {
    history.push("/");
  }
  const { brand, descriptContent, subImage } = description;
  const selectImage = subImage[index];
  const indicator = _.slice(subImage, start, end);
  const handleClickChangeIndex = currentIndex => {
    setIndex(currentIndex);
  }
  const handleClickNext = () => {
    setIndex(index + 1);
    if(index === end - 1) {
      setStart(start + 3);
      setEnd(end + 3);

    }
  }
  const handleClickPrev = () => {
    setIndex(index - 1);
    if(index === start) {
      setStart(start - 3);
      setEnd(end-3);
    }
  }
  return (
    <DetailWrapper className="detail-wrapper">
      <div className="container">
        <ProductDetailWrapper className="product-detail">
          <div className="slider-images">
            <img src={selectImage.src} alt={selectImage.src} />
            <ul className="indicator">
              {indicator.map((item,index) => <IndicatorItem
                indicatorItem={item}
                key={item.id}
                active={selectImage.id === item.id && "active"}
                handleClickChangeIndex={
                  () => handleClickChangeIndex(index)
                }
              />)}
              <li className="btn-next" onClick={handleClickNext}>
                <span>
                  <FontAwesomeIcon icon={faAngleRight} />
                </span>
              </li>
              <button
              className="btn-prev"
              onClick={handleClickPrev}
              disabled={index === 0}
              >
                <span >
                  <FontAwesomeIcon icon={faAngleLeft} />
                </span>
              </button>
            </ul>
          </div>
          <div className="select-product">

          </div>
          <div className="select-delivery">

          </div>
        </ProductDetailWrapper>
      </div>
    </DetailWrapper>
  );
}
const mapStateToProps = state => {
  return {
    isLoading: selectIsLoading(state),
    detail: selectDetail(state)
  }
}
const mapDispatchToProps = dispatch => {
  return {
    handleGetListProductDetail: data => dispatch(getListProductDetailRequest(data))
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(Detail);
