import styled from "styled-components";
export const HeaderBottomItems = styled.li`
  position: relative;
  cursor: pointer;
  z-index: 5;
  transition: 0.3s;
  &:hover > .sub-menu {
    opacity: 1;
    visibility: visible;
  }
  .item-link {
    display: block;
    padding: 0.75rem 1rem;
    color: ${(props) => props.activeColor.color};
    font-size: 1rem;
    font-weight: 700;
    text-transform: uppercase;
    transition: 0.3s;
    transition: all 0.3s linear;
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-right: 1rem;
    @media (max-width: 1200px) {
      font-size: 0.9rem;
    }
    @media (max-width: 1075px) {
      font-size: 0.8rem;
    }

    &:hover {
      color: #ee4d2d;
    }
    &:after {
      content: "${(props) => props.subMenu && "\f107"}";
      font-family: Fontawesome;
      margin-left: ${(props) => props.subMenu && "1rem"};
    }
    @media (max-width: 992px) {
      display: flex;
      justify-content: space-between;
    }

    &.active-menu {
      border-bottom: 3px solid #ee4d2d;
      color: #ee4d2d;
      font-size: 1.2rem;
    }
    & > svg {
      margin-left: 0.3rem;
    }
  }
  @media (max-width: 992px) {
    width: 100%;
    border-bottom: 1px solid #ddd;
  }
`;
export const SubMenuWrapper = styled.ul`
  min-width: 160px;
  position: absolute;
  top: 100%;
  left: 0;
  background: #fff;
  box-shadow: rgba(0, 0, 0, 0.25) 0px 0.0625em 0.0625em,
    rgba(0, 0, 0, 0.25) 0px 0.125em 0.5em,
    rgba(255, 255, 255, 0.1) 0px 0px 0px 1px inset;
  z-index: 99999;
  opacity: 0;
  visibility: hidden;
  transition: all 0.3s ease-in-out;
  transform: skewY(-180deg);
`;
