import React, { useState } from "react";
import { HeaderBottomItems, SubMenuWrapper } from "./style";
import { NavLink } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAngleDown } from "@fortawesome/free-solid-svg-icons"
import SubMenuItem from "./subMenu";

const HeaderBottomItem = props => {
    const { item, status, onClick, activeColor } = props;
    const { name, path, subMenu } = item;
    return (
        <HeaderBottomItems subMenu={subMenu} activeColor={activeColor} className="menu-item" status={status} onClick={onClick}>
            <NavLink exact={null} activeClassName="active-menu" to={item.path === 'trang-chu' ? '/' : `/${path}`} className="item-link">
                {name}

            </NavLink>
            {subMenu &&
                <SubMenuWrapper>
                    {subMenu.map(item => <SubMenuItem subMenuItem={item} key={item.id}/>)}
                </SubMenuWrapper>
            }

        </HeaderBottomItems>
    );
}
export default HeaderBottomItem;
