import React, { useEffect } from "react";
import { PostWrapper } from "./style";
import {
  getListPostRequest,
  getPostIdRequest
} from "./actions";
import {connect} from "react-redux";
import {
  selectIsLoading,
  selectPostDetail,
  selectError,
  selectPosts
} from "./selector"
import { NavLink } from "react-router-dom";
import PostItem from './../Home/Component/Post'


const Post = props => {
  const {
    handleGetListPost,
    isLoading,
    posts,
    postDetail
  } = props;
  useEffect(() => {
    handleGetListPost();
  }, []);
  console.log('posts', posts);
  return(
    <PostWrapper>
      <p className="title">
        <NavLink exact to="/" className="link-to">
          Trang chủ
        </NavLink>
        <span>
          Tất cả tin tức
        </span>
      </p>
      <h3 className="title-page">
        Tất cả tin tức
      </h3>
      <div className="container">
        <div className="row">
          {posts.map(item => {
            return <PostItem className="col-lg-4 col-md-12" postItem={item} key={item.id} />
          })}
        </div>
      </div>
    </PostWrapper>
  );
}

const mapStateToProps = state => {
  return {
    posts: selectPosts(state),
    postDetail: selectPostDetail(state),
    isLoading: selectIsLoading(state),
    error: selectError(state)
  }
}
const mapDispatch = dispatch => {
  return {
    handleGetListPost: () => dispatch(getListPostRequest()),
  }
}
export default connect(mapStateToProps, mapDispatch) (Post);
