import React, { useEffect, useState } from "react";
import Buttons from "../Button";
import Input from './../input'
import { HeaderBottomWrapper, HeaderTopWrapper, HeaderWrapper } from "./style";
import _ from 'lodash';
import { NavLink } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser } from '@fortawesome/free-regular-svg-icons';
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import { headerTopBottoms, listMenu } from './data';
import  HeaderTopBottomItem  from "./Component/headerTopBottomItem";
const Header = props => {
    const [headerTopBottom, setHeaderTopBottom] = useState([]);
    const [menus, setMenus] = useState([]);
    useEffect(() => {
        setHeaderTopBottom(headerTopBottoms);
        setMenus(listMenu);
    },[]);
    return (
        <HeaderWrapper>
            <HeaderTopWrapper className="header-top">
                    <div className="container">
                    <NavLink to="/" exact className="logo">
                        <img src="/assets/images/logo/5.png" alt="logo" />
                    </NavLink>
                    <div className="header-top-right">
                        <Input
                            type="text"
                            className="input-search"
                            placeholder="Tìm tên sản phẩm mà bạn mong muốn..."
                        />
                        <div className="account-container">
                            <FontAwesomeIcon icon={faUser} />
                            <NavLink
                                to="/dang-nhap"
                                exact
                                className="account-link"
                            >
                                <span className="account-login-logout">
                                    Đăng nhập / đăng ký
                                </span>
                                <span className="account">
                                    Tài khoản
                                </span>
                            </NavLink>

                        </div>
                        <NavLink
                            to="gio-hang"
                            exact
                            className="cart">
                            <span className="cart-icon">
                                <FontAwesomeIcon icon={faShoppingCart} />
                            </span>
                            <span className="cart-text">
                                Giỏ hàng
                            </span>
                        </NavLink>
                        <div className="header-top-bottom">
                            <ul className="list-header-top-bottom">
                            {headerTopBottom.map(item => {
                                return <HeaderTopBottomItem headerTopBottomItem={item} key={item.id}/>
                            })}
                            </ul>
                            <p className="phone">
                                0392272154 | 0376326810
                            </p>
                        </div>
                    </div>
                    </div>
                </HeaderTopWrapper>
                <HeaderBottomWrapper className="header-bottom">
                    <div className="container">
                        <ul className="list-menu">

                        </ul>
                    </div>
                </HeaderBottomWrapper>
        </HeaderWrapper>
    );
}
export default Header;
