import styled from "styled-components";
export const HomeWrapper = styled.div`
    padding-bottom: 2.5rem;
`
export const BannerWrapper = styled.div`
    width: 100%;
    height: 70vh;
    overflow: hidden;
    position: relative;
    &:after {
        width: 100%;
        height: 100%;
    }
    .slick-prev {
        left: 0;
        z-index: 6;
     
        &:before {
            min-width: 50px;
            min-height: 50px;
            opacity: 1;
            content: '\f053';
            position: absolute;
            top: 0;
            left: 0;
            font-family: 'Fontawesome' !important;
            display: flex;
            justify-content: center;
            align-items: center;
            color: #000;
            font-size: 2.5rem;
            
        }
    }
    .slick-next {
        right: 2%;
        z-index: 6;
        &:before {
            width: 50px;
            height: 50px;
            opacity: 1;
            content: '\f054';
            position: absolute;
            top: 0;
            left: 0;
            font-family: 'Fontawesome' !important;
            display: flex;
            justify-content: center;
            align-items: center;
            color: #000;
            font-size: 2.5rem;
        }
    }
    .slick-dots {
        bottom: 0;
        & > li button:before{
            opacity: 0;
        }
        & > li button:after {
            width: 12px;
            height: 12px;
            content: '';
            border: 3px solid #000;
            position: absolute;
            top: 0;
            left: 0;
            border-radius: 50%;
        }
    }

`
