import React from "react";
import { InnerItems } from "./style";
import Buttons from './../../../../Component/Button';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import ProductItem from "../ProductItem";
const InnerItem = props => {
    const { item, location } = props;
    const { titleTabs, product } = item;
    const handleToPage = () => {
        const {history} = location;
        history.push(`/`)
    }
    return (
        <InnerItems className="inner-item col-lg-3 col-md-6">
            <h4 className="name-inner">
                {titleTabs}
            </h4>
            <Slider>
                {product.map(item => <ProductItem nameButton="Xem tất cả sản phẩm" icon={item.discountContent} item={item} key={item.id} />)}
            </Slider>
           
        </InnerItems>
    );
}
export default InnerItem;
