import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Header from './Component/Header';
import Login from './Component/login';
import Register from './Component/register';
import Home from './container/Home';
import FooterWare from './container/Product/Component/FootWare';
import Footer from './Component/Footer';
import Introduce from './Component/Introduce';
import Clothes from './container/Product/Component/Clothes';
import Accessory from './container/Product/Component/Accessory';
import { useEffect, useState } from 'react';
const App = props => {
  const [scrollToTop, setScrollToTop] = useState(false);
  useEffect(() => {
    const hideScollToTop = () => {
      setScrollToTop(window.scrollY > 100);
    }
    window.addEventListener("scroll", hideScollToTop);
    return () => {
      window.removeEventListener("scroll", hideScollToTop);
    }
  }, []);
  const scrollTop = () => window.scrollTo({
    top: 0,
    behavior: 'smooth'
  })
  return(
    <div className="app">
      <Router>
        <Header/>
        {scrollToTop && <p style={{
          position: 'fixed',
          bottom: 0,
          right: '50%',
          color: 'red',
          cursor:  'pointer'
        }} onClick={scrollTop}>Scoll</p>}
        <Switch>
          <Route exact path="/" component={Home} />
          <Route exact path="/login" component={Login}></Route>
          <Route exact path="/register" component={Register}></Route>
          <Route exact path="/giới thiệu" component={Introduce}></Route>
          <Route exact path="/giày dép" component={FooterWare}></Route>
          <Route exact path="/quần áo" component={Clothes}></Route>
          <Route exact path="/phụ kiện khác" component={Accessory}></Route>
        </Switch>
        <Footer />

      </Router>
    </div>
  );
}

export default App;
