import { useState,useEffect } from 'react';
export const Loading = (bool) => {
  const [isLoading, setIsLoading] = useState(bool);
  useEffect(() => {
    setIsLoading(true);
    setTimeout(() => {
      setIsLoading(false);
    }, 3000);
  },[]);
}
