import React, { useState } from "react";
import { ProductCartItems } from "./style";
import Input from "./../../../../Component/input";
import { formatMoney } from "./../../../../Component/Format";
import Buttons from "../../../../Component/Button";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrashAlt } from "@fortawesome/free-regular-svg-icons"

const ProductCartItem = ({
  productCartItem
}) => {
  const {
    color,
    name,
    price,
    quantity,
    size,
    avatar,
    brand,
    id
  } = productCartItem;
  const [counter, setCounter] = useState(quantity)
  const formatPrice = formatMoney(price)
  const handleClickIncrement = () => {
    setCounter(counter + 1)
  }
  const handleClickDecrement = () => {
    setCounter(counter > 1 ? counter - 1 : 1)
  }
  return (
     <ProductCartItems className="product-cart-item">
      <img src={avatar} alt="" />
      <div className="info-right">
      <div className="info">
        <h4 className="name-product">
          {name}
        </h4>
        <p className="relate-info">
          Thương hiệu: {brand},
          {color && ` Nhóm màu: ${color} `},
          {size && `Size: ${size}`}
        </p>
      </div>
      <div className="price">
        <span className="price-text">
          {formatPrice}đ
        </span>
        <Buttons icon={
          <FontAwesomeIcon icon={faTrashAlt} />
        } />
      </div>
      <div className="quantity">
        <Buttons
          name="-"
          type="counter"
          className={`counter decrement ${counter === 1 && 'disabled'}`}
          onClick={handleClickDecrement}
          disabled={counter === 1}
        />
        <span className="quantity-text">
          {counter}
        </span>
        <Buttons
          name="+"
          type="counter"
          className={`counter increment ${counter === 10 && 'disabled'}`}
          onClick={handleClickIncrement}
          disabled={counter === 10}
        />
      </div>
      </div>
    </ProductCartItems>
  );
}
export default ProductCartItem;
