import styled,{keyframes} from "styled-components";
const moveBorderForm = key
export const LoginWrapper = styled.div`
    margin: 4rem;
    & > .container {
        width: 40%;
        .name-form {
            padding: 1rem;
            font-size: 2.5rem;
            text-transform: uppercase;
            color: #434E5E;
            position: relative;
            display: block;
            width: fit-content;
            margin: auto;
            &:after {
                position: absolute;
                top: 100%;
                left: 0;
                width: 10%;
                content: '';
                border: 2px solid #020202;
                background: #020202;
                animation: moveBorderForm .3s ease-in-out infinite reverse;
                
            }
        
        }
    }
`
