import React, { useState } from "react";
import { HeaderBottomItems, DropdownMenuWrapper, DropdownMenuitems } from "./style";
import { NavLink } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAngleDown } from "@fortawesome/free-solid-svg-icons"
const DropdownMenuItem = props => {
    const { item, parent } = props;

    return (
        <DropdownMenuitems className="dropdown-menu-item">
            <NavLink activeClassName="active-drop-menu" className="drop-link-menu" to={`/${parent}/${item}`}>{item}</NavLink>
        </DropdownMenuitems>
    );
}
const HeaderBottomItem = props => {
    const { item, onClick } = props;
    const { name, dropdown } = item;
    

    return (
        <HeaderBottomItems className="menu-item" onClick={onClick} >
            <NavLink exact={null} activeClassName="active-menu"   to={item.name === 'trang chủ' ? '/' : `/${name}`} className="item-link">
                {name}
                {/* {dropdown.length ? <FontAwesomeIcon icon={faAngleDown} /> : ''} */}
            </NavLink>
            
        </HeaderBottomItems>
    );
}
export default HeaderBottomItem;
