import styled, { keyframes } from "styled-components";
const moveBorderForm = keyframes`
    0% {
        left: 0;
    }
    50% {
        left: 50%;
    }
    100% {
        left: 100%;
    }
`
export const LoginWrapper = styled.div`
    background-image: url(${props => props.bgImage});
    width: 100%;
    height: 100vh;
    background-position: center center;
    background-repeat: no-repeat;
    background-size: cover;
    object-fit: cover;
    position: relative;
    &:after {
        width: 100%;
        height: 100%;
        position: absolute;
        top: 0%;
        left: 0;
        z-index: 2;
        background: rgba(0,0,0,.6);
        content: '';
    }
    & > .container .form-login-register {
        position: absolute;
        top: 20%;
        left: 50%;
        z-index: 3;
        width: 35%;
        border: 1px solid;
        transform: translate(-50%,-35%);
        padding: 1.5rem 0.55rem;
        .name-form {
            padding: 1rem 0; 
            text-align: center;
            font-weight: 550;
            text-transform: uppercase;
            color: #fff;
        }
        & > div input {
            width: 100%;
            display: block;
            outline: none;
            background: transparent;
            color: #fff;
            border: none;
            border-bottom: 1.5px solid #9d9d9d;
            padding-left: 2rem;
            position: relative;
            &::placeholder {
                color: #9d9d9d;
            }
   
            
        }
        & > .input#user , & > .input#pass {
            position: relative;
            &:after {
            position: absolute;
            top: 50%;
            left: 0;
            z-index: 3;
            transform: translateY(-50%);
            font-family: 'Fontawesome';
            color: #fff;
            margin-left: .35rem;
            }
        }
        & > .input#user:after {
            content: '\f0c0';
        }
        & > .input#pass:after {
            content: '\f023';
        }
        .forgot-pass {
            display: flex;
            justify-content: space-between;
            .checkbox {
                width: 50%;
                .input#showPass {
                    display: flex;
                    flex-direction: row-reverse;
                    justify-content: flex-end;
                    align-items: center;
                    &> input#showPass {       
                         width: auto !important;
                    }
                    & > label {
                        color: #fff;
                    }
                }
                
            }
            .forgot-pass-item {
                width: 50%;
                display: block;
            }
            
        }
        
     
    }
`
