import styled, { keyframes } from "styled-components";
const moveBorderForm = keyframes`
    0% {
        left: 0;
    }
    50% {
        left: 50%;
    }
    100% {
        left: 100%;
    }
`
export const LoginWrapper = styled.div`
    margin: 4rem;
    & > .container {
        width: 40%;
        .name-form {
            padding: 1rem;
            font-size: 2.5rem;
            text-transform: uppercase;
            color: #434E5E;
            position: relative;
            display: block;
            width: fit-content;
            margin: auto;
            &:after {
                position: absolute;
                top: 100%;
                left: 0;
                width: 20%;
                content: '';
                border: 2px solid #020202;
                background: #020202;
                animation: ${moveBorderForm} 3s linear infinite alternate-reverse;
                
            }
        
        }
        form .submit-form {
            display: flex;
            align-items: center;
            width: 100%;
            .checkbox-rememberPass{
                margin: 0 1rem;
                & > div {
                display: flex;
                flex-direction: row-reverse;
                align-items: center;
                justify-content: flex-end;
                }
             
            }
        }
        .forgot-pass {
            color: blue;
            transition: .3s;
            cursor: pointer;
            &:hover {
                text-decoration: underline;
            }
        }
        .mean-login {
            text-align: center;
            font-size: 1.5rem;
            font-weight: 500;
            color:  #434E5E;
            position: relative;
            border: 1px solid;
            overflow: hidden;
            &:after,&:before {
                position: absolute;
                top: 50%;
                margin: 0 1rem;
                width: 50%;
                content: '';
                border: 1px solid currentColor;
            }
            &:before {
                left: 0;
                margin: 0;
                width: 50%;
                transform: translateX(-18%);
            }
        }
    }
`
