import React from 'react';
import { ProductItems } from './style';
import Buttons from './../../../../Component/Button';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faShoppingCart} from '@fortawesome/free-solid-svg-icons';
import ReactHover, {Trigger, Hover} from 'react-hover';
const ProductItem = props => {
    const { item, className, icon, nameButton, onClick , type} = props;
    const { name, price, avatar, discount, status } = item;
    const priceFormat = new Intl.NumberFormat('en-US', {
        style: 'decimal',
        currency: 'USD',
    }).format(price);
    const discountFormat = new Intl.NumberFormat('en-US', {
        style: 'decimal',
        currency: 'USD',
    }).format(discount);
    const optionsCursorTrueWithMargin = {
        followCursor: true,
        shiftX: 0,
        shiftY: 20,
    }
    return (
        <ProductItems discount={discount} type = {type} className={className} src="/assets/images/logo/unnamed.png" icon={icon} status={status}>
            <span className="icon-discount">
                {icon}
            </span>
            <ReactHover options={optionsCursorTrueWithMargin}>
                <Trigger type="trigger" style={{
                    width: '100%',
                    position: 'relative'
                    }}>
                    <img style={{
                        width: '100%',
                        position: 'relative',
                        zIndex: 4
                        }} src={avatar} alt={name} />
                </Trigger>
                <Hover type="hover">
                    <div>ABC</div>
                </Hover>
            </ReactHover>
            <div className="title-product">
                <h4 className="name-product">{name}</h4>
            </div>
            <div className="price">
                <span className="discount">{discountFormat}</span>
                <span className="price-item">{priceFormat}</span>
            </div>
            <div className="status-cart">
                <span className="status">
                    {status === true ? 'Còn hàng' : 'Hết hàng'}
                </span>
                <span className="cart">
                    <FontAwesomeIcon icon={faShoppingCart}/>
                </span>
            </div>
            <Buttons onClick={onClick} name={nameButton} type="card" className="btn-card" />
        </ProductItems>
    );
}
export default ProductItem;
