import styled from "styled-components";
export const PolicyItems = styled.div`
  display: flex;
  padding: 1rem .25rem;
  .icon-policy {
    padding-right: 3rem;
    font-size: 3.1rem;
  }
  .info {
    width: 80%;
    display: flex;
    flex-wrap: wrap;
  }
`;
