import {
  call,
  takeLatest,
  takeEvery,
  put,
  fork,
  all,
  delay
} from "redux-saga/effects";
import {
  GET_LIST_POST_REQUEST,
  GET_POST_ID_REQUEST
} from "./constants";
import {
  getListPostRequest,
  getListPostFailed,
  getPostIdRequest,
  getPostIdFailed
} from "./actions";
import {
  getListPostApi,
  getPostIdApi
} from "./service";
function* getListPostProcess(params) {
  try {
    const respon = yield call(getListPostApi);
    console.log("post123=================");
  } catch (err) { }
}
function* watchListPost() {
  yield takeLatest(GET_LIST_POST_REQUEST, getListPostProcess);
}
function* watchAll() {
  yield all([
    watchListPost()
  ])
}

export default watchAll;
