import styled from "styled-components";
export const CartWrapper = styled.div`
  padding: 3rem;
  & > .container {
    display: flex;
    box-shadow: rgb(100 100 111 / 20%) 0px 7px 29px 0px;
    .payment {
      width: 25%;
      border: 1px solid;
      padding: 1rem;
      margin: 0 1rem;
    }
    .list-product-cart {
      flex: 1;
      .link-to {
        display: inline-block;
        padding: 1rem;
        border-bottom: 1px solid #cacaca;
        width: 100%;
        color: #ee4d2d;
        text-transform: uppercase;
      }
      & > table th,& > table td {
        border-bottom: 1px solid #cacaca;
      }
    }
  }
`;
