import React from 'react';
import { NavLink } from 'react-router-dom';
import { HeaderTopItems } from './style';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faShoppingCart} from '@fortawesome/free-solid-svg-icons'
const HeaderTopItem = ({headerTopItem}) => {
  const {name, path, type} = headerTopItem
  return(
    <HeaderTopItems className="head-top-item" name={name} type={type}>
      <NavLink exact to={`san-pham/`} activeClassName="active-menu">{type === "cart" ?
      <FontAwesomeIcon icon={faShoppingCart}/> : name}
      {type === "cart" ? <span className="quantity-cart">0</span> : ''}
      </NavLink>
    </HeaderTopItems>
  );
}
export default HeaderTopItem;
