import styled from "styled-components";
export const MenuItems = styled.li`

  & > .menu-link {
    display: block;
    padding: .75rem 1rem;
    color: #000;
    text-transform: uppercase;
    font-weight: 550;
    &.active {
      color: #ee4d2d;
    }
  }

`
