import { HANDLE_INIT_PRODUCT } from "./constanst";
const initialState = {
    product: []
}
const prodReducer = (state = initialState, action ) => {
    switch(action.type) {
        case HANDLE_INIT_PRODUCT: {
            console.log('ac', action.payload);
            return {
                ...state,
                product: action.payload
            }
        }
        default:
            return state;
    }
}
export default aReducer;
