import styled from "styled-components";
export const SellingItems = styled.div`
  padding: 0.15rem .35rem 2rem;
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  margin-bottom: 2rem;
  border-bottom: 1px solid
 #cacaca;
  & > img {
    width: 100%;
  }
  .title-product {
    font-size: .95rem;
    margin: .25rem;
  }
`;
