import { Switch } from "react-router";
import styled from "styled-components";
const setBackground = type => {
    switch (type) {
        case 'register': {
            return 'transparent';
        }
        case 'login': {
            return 'transparent';  
        }
        default:
            break;
    }
}
const setColor = type => {
    switch(type) {
        case 'register': {
            return 'rgba(255,255,255,0.8)';
        }
        case 'login': {
            return 'rgba(255,255,255,0.8)';
        }
        default:
    }
}
const setFontSize = type => {
    switch (type) {
        case 'register': {
            return '1.2rem';
        }
        case 'login': {
            return '1.2rem';
        }
        default:
    }
}
const setColorHover = type => {
    switch(type) {
        case 'register': {
            return 'rgba(255,255,255,1)';
        }
        case 'login': {
            return 'rgba(255,255,255,1)';
        }
        default:
    }
}
const setBorderRight = type => {
    switch(type) {
        case 'login': {
            return '2px solid #fff !important';
        }
    }
}
export const ButtonWrapper = styled.button`
    border: none !important;
    background: ${props => setBackground(props.type)};
    color: ${props => setColor(props.type)};
    font-size: ${props => setFontSize(props.type)};
    transition: .3s;
    outline: none !important;
    padding: 0.25rem .85rem;
    text-align: center;
    position: relative;
    outline: none;
    &:after {
        position: absolute;
        width: 100%;
        height: 100%;
        top: 0;
        right: 0;
        content: '';
        border-right: ${props => setBorderRight(props.type)};
    }
    &:hover {
        color: ${props => setColorHover(props.type)};
    }
   

`
