import styled from "styled-components";
export const InnerItems = styled.div`
    border: 1px solid;
    padding: 0.75rem 1rem;
    .name-inner {
        text-align: center;
        margin: 1rem 0 2rem;
        font-weight: 700;
        text-transform: uppercase;
    }
    &  img {
        width: 100%;
        max-height: 165px;
        object-fit: cover;
        display: flex;
        flex-direction: column;
        
    }
`
