import styled from "styled-components";
export const SearchProductItems = styled.div`
    margin: 0.75rem 0;
    display: flex;
    align-items: center;
    justify-content: space-between;

    &> img {
        width: 30%;
    }
    .info {
        width: 60%;
        .title-text {
        height: auto;
        overflow: hidden;
        height: ;
        .name-product {
        max-width: 82%;
        font-size: 1.2rem;
        font-weight: 550;
        color: #434E5E;
        min-height: 90px;
        cursor: pointer;
        white-space: pre-wrap; 
        overflow: hidden;
        text-overflow: ellipsis;
        -webkit-line-clamp: 3;
        -webkit-box-orient: vertical;
        display: -webkit-box;

    }
        .price {
            color: red;
            position: relative;
            font-size: 1.3rem;
            margin-left: 0.25rem;
            &:after {
                content: 'đ';
                text-decoration: underline;
            }
        }
        .discount {
            text-decoration:line-through;
            color: #434E5E;
            display: ${props => props.discount === '' ? 'none' : 'inline-block'};
            &:after {
                content: 'đ';
                text-decoration: underline;
            }
            
        }
    }
`
