import styled from "styled-components";
export const MessageCartWrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: red;
  color: #fff;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 999999999;
  display: flex;
  justify-content: center;
  align-items: center;
  .message-content {
    width: 80%;
    height: 80%;
    background: #fff;
    .close-message {
      display: block;
      padding: 1rem;
      color: #000;
      text-align: right;
      border-bottom: 1px solid #cacaca;
      & > svg {
        cursor: pointer;
        font-size: 1.5rem;
      }
    }
    & > .content {
      padding: 1rem;
      .product-to-cart {
        .message-text {
          color: #4caf50;
          font-weight: 400;
          &:before {
            content: "\f058";
            font-family: Fontawesome;
            margin-right: .35rem;
          }
        }
        .product-cart {
          color: #000;
          display: flex;
          flex-direction: column;
          & > img {
            width: 15%;
          }
        }
      }
    }
  }
`
