import styled from 'styled-components';
export const DetailWrapper = styled.div`

`
export const ProductDetailWrapper = styled.div`
  display: flex;
  width: 100%;
  .slider-images,.select-delivery {
    width: 25%;
    padding: 1.2rem 1rem;
    border: 1px solid;
  }
  .select-product {
    flex: 1;
    border: 1px solid;
  }
  .slider-images {
    & > img {
      width: 100%;
    }
    .indicator {
      display: flex;
      flex-wrap: wrap;
    }
  }
`
export const IndicatorItems = styled.li`
  width: calc((100% - 2.5rem)/3);
  margin: .45rem .5rem .45rem 0;
  & > img {
    width: 100%;
  }
`
