import React, { useState } from "react";
import { HeaderBottomItems, DropdownMenuWrapper, DropdownMenuitems } from "./style";
import { NavLink } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAngleDown } from "@fortawesome/free-solid-svg-icons"
const DropdownMenuItem = props => {
    const { item, parent, status } = props;

    return (
        <DropdownMenuitems className="dropdown-menu-item" status={status}>
            <NavLink activeClassName="active-drop-menu" className="drop-link-menu" to={`/${parent}/${item}`}>{item}</NavLink>
        </DropdownMenuitems>
    );
}
const HeaderBottomItem = props => {
    const { item, status, onClick } = props;
    const { name, dropdown } = item;
    console.log('status', status);

    return (
        <HeaderBottomItems className="menu-item"  status={status} onClick={onClick} >
            <NavLink exact={null} activeClassName="active-menu"   to={item.name === 'trang chủ' ? '/' : `/${name}`} className="item-link">
                {name}
                {dropdown.length ? <FontAwesomeIcon icon={faAngleDown} /> : ''}
            </NavLink>
               {dropdown.length ? <DropdownMenuWrapper className="drop-menu" exact status = {status} item={name}>
                {dropdown.map((item, index) => {
                    return <DropdownMenuItem parent={name} item={item} key={index} />
                })}
            </DropdownMenuWrapper> : ''}
        </HeaderBottomItems>
    );
}
export default HeaderBottomItem;
