import React, { useState } from "react";
import { TabPageWrapper,HeadTabs } from "./style";
import SelectOption from "../SelectOption";
import _ from 'lodash';
import _filter from 'lodash/filter';
import NamePage from "../NamePage";
import PaginationPage from "../../container/Footwear/Component/Pagination";
const Name = [
    {
        id: 0,
        name: 'Thứ tự mặc định'
    },
    {
        id: 1,
        name: 'Tên từ A - Z'
    },
    {
        id: 2,
        name: 'Tên từ Z - A'
    },
    {
        id: 3,
        name: 'Giá từ thấp tới cao'
    },
    {
        id: 4,
        name: 'Giá từ cao tới thấp'
    },

]
const Price = [
    {
        id: 5,
        name: 'Tất cả'
    },
    {
        id: 6,
        name: '200.000đ - 1.000.000đ'
    },
    {
        id: 7,
        name: '1.000.000đ - 2.500.000đ'
    },
    {
        id: 8,
        name: '2.500.000đ - 3.500.000đ'
    },
    {
        id: 9,
        name: '3.500.000đ - 5.000.000đ'
    },
    {
        id: 10,
        name: '5.000.000đ - 10tr'
    },
    {
        id: 11,
        name: 'Trên 10tr'
    }


]
const TabPage = props => {
    const { nameLink, to, nameTabs,  products } = props;
    // onChange select
    const [select, setSelect] = useState(0)
    // sort by name
    const [listProductSortBy, setListProductSortBy] = useState([]);
    const compareByName = (obFist, obSecond) => {
        if (obFist.name > obSecond.name) {
            return 1
        }
        else if (obFist.name < obSecond.name) {
            return -1
        }
        return 0
    }
    const compareByPrice = (obFist, obSecond) => {
        if (obFist.price > obSecond.price) {
            return 1
        }
        else if (obFist.price < obSecond.price) {
            return -1
        }
        return 0
    }
    const handleSelectChange = event => {
        const { value } = event.target;
        setSelect(+value)
        console.log('value', value);
        let newListProduct = [];       
        if (+value === 1) {
            newListProduct = [...(listProductSortBy.length ? listProductSortBy : products)].sort(compareByName);
        }
        else if (+value === 2) {
            newListProduct = _.reverse([...(listProductSortBy.length ? listProductSortBy : products)].sort(compareByName));
        }
        else if (+value === 3) {
            newListProduct = [...(listProductSortBy.length ? listProductSortBy : products)].sort(compareByPrice);
        }
        else if (+value === 4) {
            newListProduct = _.reverse( [...(listProductSortBy.length ? listProductSortBy : products)].sort(compareByPrice));
        }
        else if (+value === 6) {
            newListProduct = _.filter([...(listProductSortBy.length ? listProductSortBy : products)].filter(item => item.price <= 1000000 && item.price >= 200000))
            
        }
        else if (+value === 7) {
            newListProduct = _.filter([...(listProductSortBy.length ? listProductSortBy : products)].filter(item => item.price >= 1000000 && item.price <=2500000))
        }
        else if (+value === 8) {
            newListProduct = _.filter([...(listProductSortBy.length ? listProductSortBy : products)].filter(item => item.price >= 2500000 && item.price <=3500000))
        }
        else if (+value === 9) {
            newListProduct = _.filter([...(listProductSortBy.length ? listProductSortBy : products)].filter(item => item.price >= 3500000 && item.price <=5000000))
        }
        else if (+value === 10) {
            newListProduct = _.filter([...(listProductSortBy.length ? listProductSortBy : products)].filter(item => item.price >= 5000000 && item.price <= 10000000))
        }
        else if (+value === 11) {
            newListProduct = _.filter([...(listProductSortBy.length ? listProductSortBy : products)].filter(item => item.price >= 10000000));
        }
        else {
            newListProduct = products;
        }
        setListProductSortBy(newListProduct)
    }
    const [selectPrice, setSelectPrice] = useState(0)
    const [filterByPrice, setFilterByPrice] = useState([])
    
    const getListProductByFilter = () => {
        if (listProductSortBy.length > 1) {
            return listProductSortBy;
        }
        else if (listProductSortBy.length  <=1) {
            return <p>abc</p>
        }
        return products;
    }
    return (
        <TabPageWrapper>
            <HeadTabs className="head-tabs">
                <NamePage nameLink={nameLink} to={to} name={nameTabs} />
                <SelectOption nameSort="Lọc theo giá:" value={select} handleSelectChange={handleSelectChange} nameSelect="Name" option={Price} />
                <SelectOption nameSort="Sắp xếp theo:" value={select} handleSelectChange={handleSelectChange} nameSelect="Name" option={Name} />
            </HeadTabs>
            <PaginationPage product={getListProductByFilter()} />
        </TabPageWrapper>
    );
}
export default TabPage;
