import styled from "styled-components";
export const SellingItems = styled.div`
padding: .15rem;
  .avatar {
    width: 40%;
    border: 1px solid #ddd;
    padding: .15rem;
    &:hover > img {
      transform: scale(1);
    }
    & > img {
      width: 100%;
      cursor: pointer;
      transition: all .3s ease-in-out;
      transform: scale(0.8);

    }
  }
`;
